import { Component, ElementRef, ViewChild } from '@angular/core';
import { AppService } from 'src/app/gallery/service/app.service';

@Component({
  selector: 'shared-menubar',
  templateUrl: './menubar.component.html',
  styleUrls: ['./menubar.component.css']
})
export class MenubarComponent {

  @ViewChild('searchInput')
  public searchInput!: ElementRef<HTMLInputElement>

  constructor( private service: AppService) {}

  searchFc(){
    const key = this.searchInput.nativeElement.value;
    console.log('Belleza!!!', key);
    this.service.searchAppByKey(key);
  }
}
<nav class="navbar navbar-expand-md navbar-dark bg-dark fixed-top p-0">
    <div class="collapse navbar-collapse" id="navbarsExampleDefault" id="menubar">
        <ul class="navbar-nav mr-auto">
            <li class="nav-item active">
                <img src="assets/img/logo.png" alt="amazon store" srcset="">
            </li>
        </ul>
        <form class="form-inline my-2 my-lg-0">
            <input class="form-control mr-sm-2" type="text" placeholder="Search" aria-label="Search" #searchInput>
            <button class="btn btn-secondary my-2 my-sm-0" type="button" (click)="searchFc()">Search</button>
        </form>
    </div>
  </nav>
