import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { AppService } from '../../service/app.service';

@Component({
  selector: 'page-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomePage implements OnInit {

   private _app: any = {};

  constructor( private appService: AppService) { 
    this.appService.fetchCategories();
  }

  ngOnInit(): void {
    this.appService.loadApps();
  }

  get apps(): any[] {
    return this.appService.appList;
  }

  get categories(): any[] {
    return this.appService.categoryList;
  }

  getEmmiter(content: any): any {
    this._app = content;
  }

  get application(): any {
    return this._app;
  }

  get rate(): any {
    return this._app.rate || 0;
  }

}
<shared-menubar></shared-menubar>

<main role="main" class="container">

    <div class="row p-3">
        <div class="col-12">
          <category-list [categories]="categories"></category-list>
        </div>

        <div class="col-12 pt-2">
          <gallery-card [application]="application" [rating]="rate"></gallery-card>
          <gallery-grid [apps]="apps" (selectedApp)="getEmmiter($event)"></gallery-grid>
        </div>
    </div>

</main>
