import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'gallery-grid',
  templateUrl: './grid.component.html',
  styleUrls: ['./grid.component.css']
})
export class GridComponent {

  @Output()
  public selectedApp: EventEmitter<any> = new EventEmitter();


  @Input()
  public apps: any[] = [];

  @Input()
  public modalCaller: any;

  selectApp(app: any): void {
   this.selectedApp.emit(app);
  }

}
<div class="row">

    <div *ngFor="let app of apps"  class="col-md-3 col-sm-6" data-toggle="modal" data-target="#exampleModal">

      <div class="card mb-2 text-center bg-dark" (click)="selectApp(app)">

        <img
          [src]="app.image"
          [alt]="app.name"
          class="card-img-top"
        >

        <div class="card-body text-white">
          <p class="card-text">
            {{ app.name }}
          </p>
        </div>

      </div>

    </div>


  </div>
