import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { NgbModal, ModalDismissReasons } from '@ng-bootstrap/ng-bootstrap';
import { AppService } from '../../service/app.service';

@Component({
  selector: 'page-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomePage implements OnInit {

  @ViewChild('mymodal')
  public mymodal!: ElementRef;

  closeResult: string = '';
  constructor( private appService: AppService, private modalService: NgbModal) { 
    this.appService.fetchCategories();
  }

  ngOnInit(): void {
    console.log('onInit');
  }

  get apps(): any[] {
    return this.appService.appList;
  }

  get categories(): any[] {
    return this.appService.categoryList;
  }

  callModal(content: any): any {
    console.log('content: ', content);
    this.modalService.open(this.mymodal, {ariaLabelledBy: 'modal-basic-title'}).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return  `with: ${reason}`;
    }
  }
}
<shared-menubar></shared-menubar>

<main role="main" class="container">

    <div class="row p-3">
        <div class="col-12">
          <category-list [categories]="categories"></category-list>
        </div>

        <div class="col-12 pt-2">
          <gallery-card></gallery-card>
          <gallery-grid [apps]="apps"  [modalCaller]="show()" ></gallery-grid>

        </div>
    </div>

</main>
