import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AppService {

  public appList: any[] = [];
  public categoryList: any[] = [];

  //private apiKey:       string = 'AqMQlc7cdUXBxx75V8ssnaWtYszGfnii';
  private serviceUrl:   string = 'http://localhost:8080';
  constructor( private http: HttpClient ) { }


  searchAppByKey( key: string ):void {
    if ( key.length === 0 ) return;

    // const params = new HttpParams()
    //   .set('api_key', this.apiKey )
    //   .set('limit', '10' )
    //   .set('q', key )

    this.http.get<any>(`${ this.serviceUrl }/applications`)
      .subscribe( resp => {

        this.appList = resp;
        console.log({ gifs: this.appList });

      });






  }

  fetchCategories():void {

    this.http.get<any>(`http://localhost:8080/categories`)
      .subscribe( resp => {
        console.log('resp', resp);
        this.categoryList = resp;
      });

  }
}
