import { Component, Input } from '@angular/core';

@Component({
  selector: 'gallery-grid',
  templateUrl: './grid.component.html',
  styleUrls: ['./grid.component.css']
})
export class GridComponent {

  @Input()
  public apps: any[] = [];

  @Input()
  public modalCaller: any;

}
<div class="row">

    <div *ngFor="let app of apps"  class="col-md-3 col-sm-6">

      <div class="card mb-2 text-center bg-dark" (click)="modalCaller()">

        <img
          [src]="app.images.downsized_medium.url"
          [alt]="app.title"
          class="card-img-top"
        >

        <div class="card-body text-white">
          <p class="card-text">
            {{ app.title }}
          </p>
        </div>

      </div>

    </div>


  </div>
